mapper.select:
SELECT QNA_NO, QNA_WRITER, QNA_CONTENT, QNA_TITLE, QNA_HIT, QNA_DATE, QNA_LASTMODIFIED
  FROM QNA
 WHERE QNA_NO = #{qnaNo}

mapper.update:
		UPDATE QNA
           SET QNA_TITLE = #{qnaTitle}, QNA_CONTENT = #{qnaContent}
         WHERE QNA_NO = #{qnaNo}

UPDATE QNA 
   SET QNA_HIT = QNA_HIT + 1 
 WHERE QNA_NO = #{qnaNo}
