mapper.select:
SELECT USER_NO, ID, PW, NAME, TEL, GRADE, USER_DATE, HBD, STATE, POINT, EMAIL
  FROM USERTABLE
 WHERE ID = #{id}

SELECT USER_NO, ID, PW, NAME, TEL, GRADE, USER_DATE, HBD, STATE, POINT, EMAIL
  FROM USERTABLE
 WHERE USER_NO = #{userNo}

SELECT USER_NO, ID, PW, NAME, TEL, GRADE, USER_DATE, HBD, STATE, POINT, EMAIL
  FROM USERTABLE
 WHERE EMAIL = #{email}

SELECT USER_NO, ID, PW, NAME, TEL, GRADE, USER_DATE, HBD, STATE, POINT, EMAIL
  FROM USERTABLE
 WHERE ID = #{id} AND PW = #{pw}

mapper.update:
UPDATE USERTABLE
   SET PW = #{pw}
 WHERE ID = #{id}

UPDATE USERTABLE
   SET TEL = #{tel}
     , EMAIL = #{email}
 WHERE USER_NO = #{userNo}
